mapper.select:
select a.*, b.email, b.name, b.id
from board as a inner join member as b on a.m_id = b.id
and a.seq = #{seq}
order by a.seq desc
limit #{pageIndex}, #{perPageCount}

mapper.insert:
insert into board (
title,
content,
m_id

	,file1name
	,file1Sname

)
values (
#{title},
#{content},
#{m_id}

	,#{file1name}
	,#{file1Sname}

)
