mapper.select:
select a.id, a.title, a.content, a.file1Name, a.file1SName, a.reg_date, a.m_id, a.update_date, b.email, b.name
from board as a inner join member as b on a.m_id = b.id
and id = #{id}

mapper.insert:
insert into board (
title,
content,
m_id

	,file1name
	,file1sname

	) 
values (
#{title},
#{content},
#{m_id}

	,#{file1name}
	,#{file1sname}

)
