mapper.select:
select a.*, b.email, b.name, b.id
from board as a inner join member as b on a.m_id = b.id
a.seq = #{seq}
order by a.seq desc
limit #{pageIndex}, #{perPageCount}

mapper.insert:
insert into board (
title,
content,
m_id

	,file1Name
	,file1SName

)
values (
#{title},
#{content},
#{m_id}

	,#{file1Name}
	,#{file1SName}

)

mapper.update:
update comment
set 
comment = #{comment}
,reg_date = now()
where seq = #{seq}
